        public void ProcessRequest(HttpContext context)
        {
            _loginID = SnehBLL.UserAccount_Bll.IsLogin();
            if (_loginID <= 0)
            {
                context.Response.AddHeader("refresh", "5;/Member/");
                context.Response.ContentType = "text/html";
                context.Response.Write(str + "<div class=\"alert\">You required login to print document.</div>");
                context.Response.End();
            }
            else
            {
                int _appointmentID = 0;
                if (context.Request.QueryString["record"] != null)
                {
                    if (DbHelper.Configuration.IsGuid(context.Request.QueryString["record"].ToString()))
                    {
                        _appointmentID = SnehBLL.Appointments_Bll.Check(context.Request.QueryString["record"].ToString());
                    }
                }
                if (_appointmentID > 0)
                {
                    int _type = SnehBLL.Appointments_Bll.SessionRptType(_appointmentID);

                    if (context.Request.QueryString["type"] != null)
                    {
                        string _rType = context.Request.QueryString["type"].ToString().ToLower();

                  
                            if (_rType == "ndt")
                            {
                                NDTRPT(_appointmentID, context);
                            }
                            else if (_rType == "si")
                            {
                                SI_RPT(_appointmentID, context);
                            }
                        else if (_rType == "si_rpt2022")
                        {
                            CreatrRpt_SI2022 sI2022 = new CreatrRpt_SI2022();
                            SI_RPT2022(_appointmentID, context);
                        }
                        else if (_rType == "ndt_new")
                        {
                            NDTRPT_NEW(_appointmentID, context);
                        }
                        //if (_rType == "si")
                        //{
                        //    SI_RPT(_appointmentID, context);
                        //}

                        //else if (_rType == "si2022")
                        //{
                        //    CreatrRpt_SI2022 sI2022 = new CreatrRpt_SI2022();
                        //    SI_RPT2022(_appointmentID, context);
                        //}
                        //if (_rType == "5")
                        //{
                        //    context.Response.AddHeader("refresh", "5;/Member/");
                        //    context.Response.ContentType = "text/html";
                        //    context.Response.Write(str + "<div class=\"alert\">Implementation is in process.</div>");
                        //    context.Response.End();
                        //    //SENSORY_RPT(_appointmentID, context);
                        //}
                        else if (_rType == "7")
                        {
                            CreateRpt_PreConsult pre = new CreateRpt_PreConsult();
                            pre.PreScreenRpt(_appointmentID, context);
                        }
                        else if(_rType == "8")
                        {
                            CreateRpt_PreConsult pre_consult = new CreateRpt_PreConsult();
                            pre_consult.PreConsultRpt(_appointmentID, context);
                        }
                        //else
                        //{
                        //    context.Response.AddHeader("refresh", "5;/Member/");
                        //    context.Response.ContentType = "text/html";
                        //    context.Response.Write(str + "<div class=\"alert\">Invalid document request.</div>");
                        //    context.Response.End();
                        //}
                    }
                    else
                    {
                        if (_type == 1)         //DAILY REPORT
                        {
                            DAILYRPT(_appointmentID, context);
                        }
                        else if (_type == 2 || _type == 4)    //NDT REPORT
                        {
                            NDTRPT(_appointmentID, context);
                        }
                       
                        else if (_type == 3)    //BOTOX REPORT
                        {
                            BOTOXRPT(_appointmentID, context);
                        }
                         else if(_type==5) //EIP REPORT
                        {
                            EIPRPT(_appointmentID, context);
                         }
                            else if(_type==6) // REVAL REPORT
                        {
                            REVALRPT(_appointmentID, context);
                         }

                        else if (_type == 9)    // pre consult
                        {
                            Dwonload_CreateRpt_SI2022 sI2022 = new Dwonload_CreateRpt_SI2022();
                            SI_RPT2022(_appointmentID, context);
                        }
                        else
                        {
                            context.Response.AddHeader("refresh", "5;/Member/");
                            context.Response.ContentType = "text/html";
                            context.Response.Write(str + "<div class=\"alert\">Invalid document request.</div>");
                            context.Response.End();
                        }
                    }
                }
                else
                {
                    context.Response.AddHeader("refresh", "5;/Member/");
                    context.Response.ContentType = "text/html";
                    context.Response.Write(str + "<div class=\"alert\">Invalid document request.</div>");
                    context.Response.End();
                }
            }
        }
